//// Bubble.cs: Provides both a UITableViewCell that can be used with UITableViews// as well as a ChatBubble which is a MonoTouch.Dialog Element that can be used// inside a DialogViewController for quick UIs.//// Author://   Miguel de Icaza//using System;using System.Collections.Generic;using MonoTouch.UIKit;using MonoTouch.Dialog;using MonoTouch.CoreGraphics;using MonoTouch.Foundation;using System.Drawing;namespace BubbleCell{	internal abstract class BubbleCell : UITableViewCell 	{		protected UIView CellView {get; private set;}		protected abstract UIView BubbleImageView {get;}		protected abstract SizeF BubbleImageSize { get; }		protected bool isLeft;		protected BubbleCell ( bool isLeft, UITableViewCellStyle uITableViewCellStyle, NSString nSString ) :			base (uITableViewCellStyle, nSString)		{			this.isLeft = isLeft;			var rect = new RectangleF ( 0, 0, 1, 1 );			this.isLeft = isLeft;			CellView = new UIView ( rect );			CellView.AddSubview ( BubbleImageView );						ContentView.Add ( CellView );		}				public override void LayoutSubviews ()		{			base.LayoutSubviews ();			var frame = ContentView.Frame;			var size = BubbleImageSize;			BubbleImageView.Frame = new RectangleF ( new PointF ( isLeft ? 10 : frame.Width - size.Width - 10, frame.Y ), size );			CellView.SetNeedsDisplay ( );		}	}}